# -*- coding: utf-8 -*-
import jieba
import jieba.analyse as ja
import heapq
import os


class NLPTools:
    root_dir = './'

    def __init__(self, stop_word_file=True):
        self.__set_stop_words(stop_word_file)
        if not os.path.exists(self.root_dir + 'conf'):
            self.root_dir = '../'

        # 加载自定义词典
        dict_dir = self.root_dir + "conf/dict"
        if os.path.exists(dict_dir):
            for dirnames, paths, filenames in os.walk(dict_dir):
                for file in filenames:
                    jieba.load_userdict(dirnames + '/' + file)

    def __set_stop_words(self, stop_word_file):
        if type(stop_word_file) is str and os.path.exists(stop_word_file):
                self.set_stop_words(stop_word_file)
        elif type(stop_word_file) is bool and not stop_word_file:
            return
        else:
            # 找默认的停用词文件
            stop_word_file = self.root_dir + 'conf/stopwords.txt'
            if os.path.exists(stop_word_file):
                ja.set_stop_words(stop_word_file)
            else:
                return

    def extract_keywords(self, content, topK=20, use_tfidf=True, do_regularization=True):
        """
        抽取关键词的功能，集成了jieba的tfidf和textrank
        tfidf 适合很短的文本，和词频比较相关
        textrank 适合相对长的文本，可能会出现一些相对低频但有意义的词
        :param content:
        :param topK:
        :param use_tfidf: 是否使用tfidf算法，如果不用那么仅使用textrank
        :param do_regularization: 是否正则化
        :return:
        """

        final_words = {}
        times = 2.0

        if use_tfidf:
            tfidf_keywords = ja.extract_tags(content, withWeight=True, topK=topK)
            if tfidf_keywords:
                sum_weights = sum(list(zip(*tfidf_keywords))[1])
                for word, weight in tfidf_keywords:
                    final_words[word] = weight / sum_weights * times

        new_topK = int(topK/(2*times)) if use_tfidf else topK
        textrank_keywords = ja.textrank(content, withWeight=True, topK=new_topK)
        if textrank_keywords:
            sum_weights = sum(list(zip(*textrank_keywords))[1])
            for word, weight in textrank_keywords:
                if word in final_words:
                    # 两种方法的权重相加
                    final_words[word] += weight / sum_weights
                else:
                    final_words[word] = weight / sum_weights

        topk_items = []
        if final_words:
            topk_items = heapq.nlargest(topK, final_words.items(), key=lambda x: x[1])
            if do_regularization:
                # 正则化，返回结果的权重之和为1
                weights = list(zip(*topk_items))[1]
                sum_weights = sum(weights)
                new_weights = [weight/sum_weights for weight in weights]
                words = list(zip(*topk_items))[0]

                topk_items = list(zip(words, new_weights))

        return topk_items

